BOOL_T fdrySntpServerTable_OidIndexToData(UI32_T exact, UI32_T compc,
    oid *compl, UI32_T *fdrySntpServerIndex)
{
    /* get or set
     */
    if (exact)
    {
        /* check the index length
         */
        if (compc != FDRYSNTPSERVERENTRY_INSTANCE_LEN) /* the constant size index */
        {
            return FALSE;
        }
    }

    *fdrySntpServerIndex = compl[0];

    return TRUE;
}

