unsigned char *var_fdrySntpServerTable(struct variable *vp,
    oid *name,
    size_t *length,
    int exact,
    size_t *var_len,
    WriteMethod **write_method)
{
    UI32_T compc = 0;
    oid compl[FDRYSNTPSERVERENTRY_INSTANCE_LEN] = {0};
    oid best_inst[FDRYSNTPSERVERENTRY_INSTANCE_LEN] = {0};

    /* table-specific variables
     */
    L_INET_AddrIp_T entry;
    UI32_T index = 0;

    /* Give default value for the record first.
     * We use the row_status = 0 to indicate if this set operation is Set by field
     * or set by record (included row creation). If the row_status == 0, this record
     * mean set by field; otherwise, we just think it is a normal set operation.
     */
    memcpy(&fdry_sntp_inet_addr_str, &fdry_sntp_zero_ipv4_addr_str, sizeof(L_INET_AddrIp_T));
    fdry_sntp_row_status = 0;

    /* dispatch node to set write method
     */
    switch (vp->magic)
    {
        case LEAF_fdrySntpServerAddrType:
            *write_method = write_fdrySntpServerAddrType;
            break;

        case LEAF_fdrySntpServerAddr:
            *write_method = write_fdrySntpServerAddr;
            break;

        case LEAF_fdrySntpServerVersion:
            *write_method = write_fdrySntpServerVersion;
            break;

        case LEAF_fdrySntpServerRowStatus:
            *write_method = write_fdrySntpServerRowStatus;
            break;

        default:
            *write_method = 0;
            break;
    }

    /* check compc, retrive compl
     */
    SNMP_MGR_RetrieveCompl(vp->name, vp->namelen, name, *length, &compc, compl,
        FDRYSNTPSERVERENTRY_INSTANCE_LEN);

    memset(&entry, 0, sizeof(entry));

    /* dispatch get-exact versus get-next
     */
    if (exact) /* get or set */
    {
        /* extract index
         */
        if (! fdrySntpServerTable_OidIndexToData(exact, compc, compl,
            &index))
        {
            return NULL;
        }

        /* get-exact from core layer
         */
        if (! SNTP_PMGR_GetServerIp(index, &entry))
        {
            return NULL;
        }
    }
    else /* get-next */
    {
        /* extract index
         */
        fdrySntpServerTable_OidIndexToData(exact, compc, compl,
            &index);

        /* Check the length of inputing index. If compc is less than instance
         * length, we should try get {A.B.C.0.0...}, where A.B.C was
         * obtained from the "..._OidIndexToData" function call, and
         * 0.0... was initialized in the beginning of this function.
         * This instance may exist in the core layer.
         */
        if (compc < FDRYSNTPSERVERENTRY_INSTANCE_LEN)  /* incomplete index */
        {
            /* get-exact, in case this instance exists
             */
            if (! SNTP_PMGR_GetServerIp(index, &entry))
            {
                /* get-next according to lexicographic order; if none, fail
                 */
                if (! SNTP_PMGR_GetNextServerIp(&index, &entry))
                {
                    return NULL;
                }
            }
        }
        else  /* complete index */
        {
            /* get-next according to lexicographic order; if none, fail
             */
            if (! SNTP_PMGR_GetNextServerIp(&index, &entry))
            {
                return NULL;
            }
        }
    }

    /* copy base OID (without index) to output
     */
    memcpy(name, vp->name, vp->namelen * sizeof(oid));

    /* assign data to the OID index
     */
    best_inst[0] = index;
    memcpy(name + vp->namelen, best_inst, FDRYSNTPSERVERENTRY_INSTANCE_LEN * sizeof(oid));
    *length = vp->namelen + FDRYSNTPSERVERENTRY_INSTANCE_LEN;

    /* dispatch node to read value
     */
    switch (vp->magic)
    {
#if (SYS_ADPT_PRIVATEMIB_INDEX_ACCESSIBLE == 1)
        case LEAF_fdrySntpServerIndex:
            *var_len = 4;
            long_return = index;
            return (u_char *) &long_return;
#endif /* #if (SYS_ADPT_PRIVATEMIB_INDEX_ACCESSIBLE == 1) */

        case LEAF_fdrySntpServerAddrType:
            *var_len = 4;
            long_return = entry.type;
            return (u_char *) &long_return;

        case LEAF_fdrySntpServerAddr:
        {
            UI32_T type, addr_len, preflen;

            if (! SNMP_MGR_ConvertLInetAddrToInetAddrAndType(
                (L_INET_Addr_T *) &entry,
                &type, &addr_len, return_buf, &preflen))
            {
                /* conversion failed
                 */
                *var_len = 0;
            }
            else
            {
                *var_len = (size_t) addr_len;
            }

            return (u_char *) return_buf;
        }

        case LEAF_fdrySntpServerVersion:
            *var_len = 4;
            long_return = 3;  /* hard-coded */
            return (u_char *) &long_return;

        case LEAF_fdrySntpServerRowStatus:
            *var_len = 4;
            long_return = VAL_sntpServerStatus_valid;
            return (u_char *) &long_return;

        default:
            ERROR_MSG("");
            break;
    }

    /* return failure
     */
    return NULL;
}

int write_fdrySntpServerAddrType(int action,
    u_char *var_val,
    u_char var_val_type,
    size_t var_val_len,
    u_char *statP,
    oid *name,
    size_t name_len)
{
    /* dispatch action
     */
    switch (action)
    {
        case RESERVE1:
            /* check type and length
             */
            if (var_val_type != ASN_INTEGER)
            {
                return SNMP_ERR_WRONGTYPE;
            }
            break;

        case RESERVE2:
            /* check valid values
             */
            switch (*(long *) var_val)
            {
                case VAL_fdrySntpServerAddrType_unknown:
                   return SNMP_ERR_WRONGVALUE;

                case VAL_fdrySntpServerAddrType_ipv4:
                case VAL_fdrySntpServerAddrType_ipv6:
                case VAL_fdrySntpServerAddrType_ipv4z:
                case VAL_fdrySntpServerAddrType_ipv6z:
                    break;

                case VAL_fdrySntpServerAddrType_dns:
                default:
                    return SNMP_ERR_WRONGVALUE;
            }

            /* Cache IP type in SNMP before setting IP.
             * When set IP address, it will be set to core layer together.
             * It is ported from Galaxy.
             */
            fdry_sntp_addr_type = *(long *) var_val;

            break;

        case FREE:
            break;

        case ACTION:
        {
            UI32_T oid_name_length = 15;

            /* table-specific variables
             */
            UI32_T fdrySntpServerIndex = 0;
            L_INET_AddrIp_T entry;

            /* extract index
             */
            if (! fdrySntpServerTable_OidIndexToData(TRUE,
                name_len - oid_name_length,
                &(name[oid_name_length]),
                &fdrySntpServerIndex))
            {
                return SNMP_ERR_COMMITFAILED;
            }

            /* For row creation, if row_status == 0, means that this is a normal
             * operation, we do the normal set operation; otherwise it is a row
             * creation operation, will do nothing here, and the overall entry
             * will perform in the write_xxxRowStatus function.
             */
            if (fdry_sntp_row_status == 0)
            {
                /* get from core layer
                 */
                if (! SNTP_PMGR_GetServerIp(fdrySntpServerIndex, &entry))
                {
                    return NULL;
                }

                /* MIB object description says that if entry is active,
                 * modification is not allowed.
                 * If write the same value, don't call the core layer for
                 * modification.
                 */
                if (fdry_sntp_addr_type != entry.type)
                {
                    return SNMP_ERR_INCONSISTENTVALUE;
                }
                else
                {
                    return SNMP_ERR_NOERROR;
                }

                /* nothing to store
                 */
            }

            break;
        }

        case UNDO:
            break;

        case COMMIT:
            break;
    }

    /* return success
     */
    return SNMP_ERR_NOERROR;
}

int write_fdrySntpServerAddr(int action,
    u_char *var_val,
    u_char var_val_type,
    size_t var_val_len,
    u_char *statP,
    oid *name,
    size_t name_len)
{
    /* dispatch action
     */
    switch (action)
    {
        case RESERVE1:
            /* check type and length
             */
            if (var_val_type != ASN_OCTET_STR)
            {
                return SNMP_ERR_WRONGTYPE;
            }
            if ((var_val_len < MINSIZE_fdrySntpServerAddr)
                || (var_val_len > MAXSIZE_fdrySntpServerAddr))
            {
                return SNMP_ERR_WRONGLENGTH;
            }
            break;

        case RESERVE2:
            /* NOTE: InetAddressType and InetAddress
             *
             * The following checks use the corresponding InetAddressType.
             * If corresponding InetAddressType and InetAddress are in the
             * same packet, this check must be done in the sequence of
             * RESERVE1->RESERVE2->ACTION after InetAddressType is stored.
             */

            /* check address length based on address type
             */
            if (! SNMP_MGR_IsInetAddrLengthValid(
                fdry_sntp_addr_type,
                (UI32_T) var_val_len))
            {
                return SNMP_ERR_INCONSISTENTVALUE;
            }

            /* check address value based on address type
             */
            if (! SNMP_MGR_IsInetAddrValueValid(
                fdry_sntp_addr_type,
                var_val))
            {
                return SNMP_ERR_INCONSISTENTVALUE;
            }

            /* store to row-create structure
             */
            if (! SNMP_MGR_ConvertInetAddrAndTypeToLInetAddr(fdry_sntp_addr_type,
                var_val_len, var_val, 0,
                (L_INET_Addr_T *) &fdry_sntp_inet_addr_str))
            {
                return SNMP_ERR_COMMITFAILED;
            }

            /* clear InetAddressType to wait for next write;
             * the Brocade default is ipv4(1)
             */
            fdry_sntp_addr_type = VAL_fdrySntpServerAddrType_ipv4;

            break;

        case FREE:
            break;

        case ACTION:
        {
            UI32_T oid_name_length = 15;

            /* table-specific variables
             */
            UI32_T fdrySntpServerIndex = 0;
            L_INET_AddrIp_T entry;

            /* extract index
             */
            if (! fdrySntpServerTable_OidIndexToData(TRUE,
                name_len - oid_name_length,
                &(name[oid_name_length]),
                &fdrySntpServerIndex))
            {
                return SNMP_ERR_COMMITFAILED;
            }

            /* For row creation, if row_status == 0, means that this is a normal
             * operation, we do the normal set operation; otherwise it is a row
             * creation operation, will do nothing here, and the overall entry
             * will perform in the write_xxxRowStatus function.
             */
            if (fdry_sntp_row_status == 0)
            {
                /* get from core layer
                 */
                if (! SNTP_PMGR_GetServerIp(fdrySntpServerIndex, &entry))
                {
                    return NULL;
                }

                /* MIB object description says that if entry is active,
                 * modification is not allowed.
                 * If write the same value, don't call the core layer for
                 * modification.
                 */
                if (! L_INET_IsEqual(&fdry_sntp_inet_addr_str, &entry))
                {
                    return SNMP_ERR_INCONSISTENTVALUE;
                }
                else
                {
                    return SNMP_ERR_NOERROR;
                }

#if 0  /* don't allow modification */
                /* set to core layer
                 */
                if (! SNTP_PMGR_AddServerIp(fdrySntpServerIndex, &fdry_sntp_inet_addr_str))
                {
                    return SNMP_ERR_COMMITFAILED;
                }
#endif
            }

            break;
        }

        case UNDO:
            break;

        case COMMIT:
            break;
    }

    /* return success
     */
    return SNMP_ERR_NOERROR;
}

