int write_fdrySntpServerRowStatus(int action,
    u_char *var_val,
    u_char var_val_type,
    size_t var_val_len,
    u_char *statP,
    oid *name,
    size_t name_len)
{
    /* dispatch action
     */
    switch (action)
    {
        case RESERVE1:
            /* check type and length
             */
            if (var_val_type != ASN_INTEGER)
            {
                return SNMP_ERR_WRONGTYPE;
            }
            break;

        case RESERVE2:
            /* check valid values
             */
            /* The core layer only has valid (create) and invalid (destroy).
             * So, we have partial support of RowStatus.
             * 1. Writing "notInService(2)" is not supported.
             * 2. Writing "createAndWait(5)" makes it active(1), so that
             * the sequence "createAndWait(5), write field, active(1)",
             * succeeds and results in active(1) in the end, as expected.
             */
            switch (*(long *) var_val)
            {
                case VAL_fdrySntpServerRowStatus_active:
                    break;

                case VAL_fdrySntpServerRowStatus_notInService:
                case VAL_fdrySntpServerRowStatus_notReady:
                    return SNMP_ERR_WRONGVALUE;

                case VAL_fdrySntpServerRowStatus_createAndGo:
                case VAL_fdrySntpServerRowStatus_createAndWait:
                case VAL_fdrySntpServerRowStatus_destroy:
                    break;

                default:
                    return SNMP_ERR_WRONGVALUE;
            }

            /* In reserve2, we overwrite the set value to the row-create entry
             */
            memcpy(&fdry_sntp_row_status, var_val, sizeof(UI32_T));
            break;

        case FREE:
            break;

        case ACTION:
        {
            UI32_T oid_name_length = 15;

            /* table-specific variables
             */
            UI32_T fdrySntpServerIndex = 0;
            L_INET_AddrIp_T ip_addr;

            /* extract index
             */
            if (! fdrySntpServerTable_OidIndexToData(TRUE,
                name_len - oid_name_length,
                &(name[oid_name_length]),
                &fdrySntpServerIndex))
            {
                return SNMP_ERR_COMMITFAILED;
            }

            /* set to core layer
             */
            /* The core layer only has valid (create) and invalid (destroy).
             * So, we have partial support of RowStatus.
             * 1. Writing "notInService(2)" is not supported.
             * 2. Writing "createAndWait(5)" makes it active(1), so that
             * the sequence "createAndWait(5), write field, active(1)",
             * succeeds and results in active(1) in the end, as expected.
             */
            switch (fdry_sntp_row_status)
            {
                /* active: accepted only if it already exists
                 */
                case VAL_fdrySntpServerRowStatus_active:
                    if (! SNTP_PMGR_GetServerIp(fdrySntpServerIndex, &ip_addr))
                    {
                        return SNMP_ERR_COMMITFAILED;
                    }
                    break;

                /* not supported: should not come here; already filtered in RESERVE2
                 */
                case VAL_fdrySntpServerRowStatus_notInService:
                case VAL_fdrySntpServerRowStatus_notReady:
                    return SNMP_ERR_WRONGVALUE;

                /* create: always results in a valid (active) entry
                 *
                 * REFERENCE: PRIVATE-MIB.sntpServerStatus
                 */
                case VAL_fdrySntpServerRowStatus_createAndGo:
                case VAL_fdrySntpServerRowStatus_createAndWait:
                {
                    /* it does not allow overwriting valid existing entry
                     */
                    if (SNTP_PMGR_GetServerIp(fdrySntpServerIndex, &ip_addr))
                    {
                        return SNMP_ERR_COMMITFAILED;
                    }

                    /* if the user has specified an address during row-creation,
                     * add it to the core layer
                     */
                    if (memcmp(&fdry_sntp_inet_addr_str, &fdry_sntp_zero_ipv4_addr_str,
                        sizeof(L_INET_AddrIp_T)) != 0)
                    {
                        if (! SNTP_PMGR_AddServerIp(fdrySntpServerIndex, &fdry_sntp_inet_addr_str))
                        {
                            return SNMP_ERR_COMMITFAILED;
                        }
                    }

                    /* if the user has not specified an address during row-creating,
                     * generate a default IPv4 address
                     */
                    else
                    {
                        UI32_T i;
                        UI32_T host_ip = SNTP_DEFAILT_SERVER_IP;
                        UI32_T network_ip;

                        /* assign default IP address (use ipv4 start from 10.1.0.1 to 10.1.0.3)
                         * if default IP already exist, it will add 1.
                         */
                        memcpy(&ip_addr, &fdry_sntp_zero_ipv4_addr_str, sizeof(L_INET_AddrIp_T));

                        for (i = 0; i < MAX_sntpServerIndex; i++)
                        {
                            network_ip = L_STDLIB_Hton32(host_ip);
                            memcpy(ip_addr.addr, &network_ip, SYS_TYPE_IPV4_ADDR_LEN);

                            if (SNTP_PMGR_AddServerIp(fdrySntpServerIndex, &ip_addr))
                            {
                                break;
                            }
                            else
                            {
                                host_ip++;
                            }
                        }
                    }

                    break;
                }

                /* destroy: even if non-existent, the core layer should return success
                 * (RFC 2579 SNMPv2-TC)
                 */
                case VAL_fdrySntpServerRowStatus_destroy:
                    if (! SNTP_PMGR_DeleteServerIpForSNMP(fdrySntpServerIndex))
                    {
                        return SNMP_ERR_COMMITFAILED;
                    }
                    break;

                default:
                    return SNMP_ERR_WRONGVALUE;
            }

            break;
        }

        case UNDO:
            break;

        case COMMIT:
            break;
    }

    /* return success
     */
    return SNMP_ERR_NOERROR;
}
#endif  /* (SYS_CPNT_SNTP == TRUE); from near-beginning to end of file */
